.pulsating-btn {
  /* Style de base (utilise tes variables) */
  padding: var(--spacing-m) var(--spacing-l);
  border-radius: var(--radius-m);
  background-color: var(--color-accent-primary);
  color: var(--neutral5);
  font-weight: bold;
  letter-spacing: 1.5px;
  cursor: pointer;

  /* Transition pour l'effet */
  transition: all var(--transition-normal);

  /* Reset button par défaut */
  border: 1px solid transparent; 
}

/* Effet de survol */
.pulsating-btn:hover {
  /* On fonce la couleur */
  background-color: var(--color-accent-primary-dark);
  /* Un léger effet d'agrandissement */
  transform: scale(1.05); 
  /* Et l'ombre pour la profondeur */
  box-shadow: 0 0 15px 5px var(--transparent60);
}

/* Effet de clic (actif) */
.pulsating-btn:active {
  transform: scale(0.98);
}

